// export default AdminPanel;

import React, { useState } from 'react';
import innovationImage from '../assets/innovation.jpg'; // Adjust path if needed

const AdminPanel = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  return (
    <div className="container mt-5">
      <h1>Admin Panel</h1>

      {/* Selection Buttons */}
      <div className="btn-group mt-3">
        <button 
          className={`btn ${selectedOption === "posted" ? "btn-primary" : "btn-outline-primary"}`} 
          onClick={() => setSelectedOption("posted")}
        >
          Approve Posted Opportunity
        </button>
        <button 
          className={`btn ${selectedOption === "users" ? "btn-primary" : "btn-outline-primary"}`} 
          onClick={() => setSelectedOption("users")}
        >
          Approve New Users
        </button>
      </div>

      {/* Display Based on Selection */}
      {selectedOption === "posted" && (
        <div className="card mt-4 shadow-sm">
          <img 
            src={innovationImage} 
            alt="Innovation Lab" 
            className="card-img-top" 
            style={{ height: '300px', objectFit: 'cover' }}
          />
          <div className="card-body">
            <h5 className="card-title">Innovation Lab Internship Opportunity</h5>
            <p className="card-text">
              Check out this possible internship opportunity with the Innovation Lab! 
              Gain hands-on experience with <strong>3D Printing, the Woodshed, and more</strong>.
            </p>
          </div>
        </div>
      )}

      {selectedOption === "users" && (
        <div className="mt-4">
          <p>Here you can approve new users.</p>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
