import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Dummy user data
const users = [
  {
    id: 1,
    name: 'Chelsea Lynch',
    major: 'Computer Science',
    school: 'Stetson University',
    employmentStatus: 'Student Employee',
    location: 'Deland, FL',
    profilePhoto: 'https://images.app.goo.gl/8RrCPdrK5hNVqhS26', // Placeholder image
  },
  {
    id: 2,
    name: 'Lui McFarlane',
    major: 'Engineering',
    school: 'Stetson University',
    employmentStatus: 'Student Intern',
    location: 'Orlando, FL',
    profilePhoto: 'https://images.app.goo.gl/8RrCPdrK5hNVqhS26', // Placeholder image
  },
  {
    id: 3,
    name: 'Kim McFarlane',
    major: 'Cybersecurity',
    school: 'Stetson University',
    employmentStatus: 'Employed',
    location: 'Apopka, FL',
    profilePhoto: 'https://images.app.goo.gl/8RrCPdrK5hNVqhS26', // Placeholder image
  },
  {
    id: 4,
    name: 'Keely Reid',
    major: 'Economics',
    school: 'Stetson University',
    employmentStatus: 'Employed',
    location: 'Deltona, FL',
    profilePhoto: 'https://images.app.goo.gl/8RrCPdrK5hNVqhS26', // Placeholder image
  },
  // Add more users as needed
];

const User = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 3;

  // Calculate the index range for the current page of users
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);

  // Total pages based on usersPerPage
  const totalPages = Math.ceil(users.length / usersPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="container mt-5">
      <h1>Users</h1>
      <div className="row">
        {currentUsers.map((user) => (
          <div key={user.id} className="col-md-4 mb-4">
            <div className="card">
              <img
                src={user.profilePhoto}
                alt={user.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title">{user.name}</h5>
                <p className="card-text">
                  Major: {user.major}<br />
                  School: {user.school}<br />
                  Employment Status: {user.employmentStatus}<br />
                  Location: {user.location}
                </p>
                <Link to={`/user/${user.id}`} className="btn btn-primary">View Profile</Link>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <nav>
        <ul className="pagination justify-content-center">
          {[...Array(totalPages)].map((_, index) => (
            <li key={index + 1} className={`page-item ${currentPage === index + 1 ? 'active' : ''}`}>
              <button className="page-link" onClick={() => paginate(index + 1)}>
                {index + 1}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default User;
