import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function User() {
    const [users, setUsers] = useState([]);

    // Fetch users from the API when the component mounts
    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        const token = localStorage.getItem('token'); // Retrieve authentication token
        try {
            const response = await fetch('http://138.197.99.80:2490/api/users', {
                method: 'GET',
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.ok) {
                const data = await response.json(); // Parse the response data
                setUsers(data); // Set the users data
            } else {
                console.error("Failed to fetch users. Status:", response.status);
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    return (
        <div className="min-vh-100 w-100">
            <Container className="mt-5">
                <Row>
                    {users.length === 0 ? (
                        <Col>
                            <p>Loading users...</p>
                        </Col>
                    ) : (
                        users.map((user) => (
                            <Col md={4} key={user._id}>
                                <Card style={{ marginBottom: '20px' }}>
                                    <Card.Body>
                                        <Card.Title>{user.first_name} {user.last_name}</Card.Title>
                                        <Card.Text><strong>Major:</strong> {user.major}</Card.Text>
                                        <Card.Text><strong>Company:</strong> {user.company}</Card.Text>
                                        <Link to={`/user/${user._id}`}>
                                            <Button variant="primary">View Profile</Button>
                                        </Link>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))
                    )}
                </Row>
            </Container>
        </div>
    );
}

export default User;
