// export default Home;

import React, { useState } from 'react';

// Sample Data for Stetson News and Events (You can replace this with data from an API)
const events = [
  {
    id: 1,
    title: 'Stetson Annual Career Fair',
    date: 'April 15, 2025',
    description: 'Join us for the annual career fair where you can meet employers and find job opportunities.',
    image: '/assets/career-fair.jpg', // Replace with actual image path
  },
  {
    id: 2,
    title: 'Spring Concert at Stetson',
    date: 'April 22, 2025',
    description: 'Enjoy a lively spring concert performed by Stetson students and local artists.',
    image: '/assets/spring-concert.jpg', // Replace with actual image path
  },
  {
    id: 3,
    title: 'Stetson Community Service Day',
    date: 'April 30, 2025',
    description: 'Help give back to the community with a day of service and volunteer opportunities.',
    image: '/assets/community-service.jpg', // Replace with actual image path
  },
  {
    id: 4,
    title: 'Stetson Alumni Reunion',
    date: 'May 5, 2025',
    description: 'Reconnect with Stetson alumni and make lasting memories at the annual alumni reunion event.',
    image: '/assets/alumni-reunion.jpg', // Replace with actual image path
  },
  {
    id: 5,
    title: 'Stetson Spring Festival',
    date: 'May 12, 2025',
    description: 'A celebration of spring with food, music, and fun for all Stetson students and staff.',
    image: '/assets/spring-festival.jpg', // Replace with actual image path
  },
  {
    id: 6,
    title: 'Graduation Ceremony',
    date: 'May 20, 2025',
    description: 'Celebrate the achievements of Stetson graduates at the annual graduation ceremony.',
    image: '/assets/graduation.jpg', // Replace with actual image path
  },
];

const Home = () => {
  const eventsPerPage = 3;
  const [currentPage, setCurrentPage] = useState(1);

  // Calculate the events to display for the current page
  const indexOfLastEvent = currentPage * eventsPerPage;
  const indexOfFirstEvent = indexOfLastEvent - eventsPerPage;
  const currentEvents = events.slice(indexOfFirstEvent, indexOfLastEvent);

  // Handle page change
  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="container mt-5">
      <h1>Welcome to Stetson University!</h1>
      <p>Keep up to date with what's happening at Stetson.</p>

      <div className="row">
        {currentEvents.map((event) => (
          <div className="col-md-4" key={event.id}>
            <div className="card mb-4 shadow-sm">
              <img
                src={event.image}
                alt={event.title}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title">{event.title}</h5>
                <p className="card-text">{event.description}</p>
                <p className="text-muted">Date: {event.date}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Pagination Controls */}
      <div className="d-flex justify-content-center mt-4">
        <button
          className="btn btn-outline-secondary mx-2"
          onClick={() => handlePageClick(1)}
          disabled={currentPage === 1}
        >
          1
        </button>
        <button
          className="btn btn-outline-secondary mx-2"
          onClick={() => handlePageClick(2)}
          disabled={currentPage === 2}
        >
          2
        </button>
        <button
          className="btn btn-outline-secondary mx-2"
          onClick={() => handlePageClick(3)}
          disabled={currentPage === 3}
        >
          3
        </button>
      </div>
    </div>
  );
};

export default Home;
