import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { Button } from "./Components/Button.js";
import Home from "./Pages/Home";
import Users from "./Pages/User";
import Opportunities from "./Pages/Opportunities";
import Profile from "./Pages/Profile";
import AdminPanel from "./Pages/AdminPanel";
import LoginModal from "./Components/LoginModal";
import SignupModal from "./Components/SignupModal";
import "./index.css";

function App() {
  const [userRole, setUserRole] = useState("guest"); // guest, user, admin
  const [isLoginModalOpen, setLoginModalOpen] = useState(false);
  const [isSignupModalOpen, setSignupModalOpen] = useState(false);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 p-4">
        <nav className="flex justify-between bg-blue-600 p-4 text-white rounded-lg">
          <Link to="/" className="font-bold">Home</Link>
          <div className="space-x-4">
            {userRole !== "guest" && <Link to="/users">Users</Link>}
            {userRole !== "guest" && <Link to="/opportunities">Opportunities</Link>}
            {userRole !== "guest" && <Link to="/profile">Profile</Link>}
            {userRole === "admin" && <Link to="/admin">Admin Panel</Link>}
          </div>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          {userRole !== "guest" && <Route path="/users" element={<Users />} />}
          {userRole !== "guest" && <Route path="/opportunities" element={<Opportunities />} />}
          {userRole !== "guest" && <Route path="/profile" element={<Profile />} />}
          {userRole === "admin" && <Route path="/admin" element={<AdminPanel />} />}
        </Routes>

        <div className="fixed bottom-4 left-4">
          <Button onClick={() => setUserRole("guest")} className="mr-2">Guest</Button>
          <Button onClick={() => setUserRole("user")} className="mr-2">User</Button>
          <Button onClick={() => setUserRole("admin")}>Admin</Button>
        </div>

        {userRole === "guest" && (
          <div className="fixed bottom-4 right-4 space-x-2">
            <Button onClick={() => setLoginModalOpen(true)}>Login</Button>
            <Button onClick={() => setSignupModalOpen(true)}>Signup</Button>
          </div>
        )}

        {isLoginModalOpen && <LoginModal onClose={() => setLoginModalOpen(false)} />}
        {isSignupModalOpen && <SignupModal onClose={() => setSignupModalOpen(false)} />}
      </div>
    </Router>
  );
}

export default App;
