import React, { useState } from 'react';

// Sample data for opportunities (this can be fetched from an API)
const opportunities = [
  {
    id: 1,
    postedBy: 'Johnson Corp',
    type: 'Full-time job',
    description: 'A regular 9-5 but you also work on special projects on Saturdays.',
    contact: 'tjohnson@gmail.com',
    detailedDescription: 'A bonus is given when you work on Saturdays.\nContact: tjohnson@gmail.com',
    isExpanded: false,
  },
  {
    id: 2,
    postedBy: 'Henry I.T',
    type: 'Internship',
    description: 'Gain hands on experience while being a valuable team member.',
    contact: 'ahenry',
    detailedDescription: 'Internship position for summer.\nContact: ahenry@gmail.com',
    isExpanded: false,
  },
  {
    id: 3,
    postedBy: 'Stetson University',
    type: 'Student Worker',
    description: 'Gain work experiences while being a student.',
    contact: 'stetsonuni',
    detailedDescription: 'On-campus job opportunities for students who need to work while studying.\nContact: stetsonuni@gmail.com',
    isExpanded: false,
  },
  {
    id: 4,
    postedBy: 'Google Inc.',
    type: 'Full-time job',
    description: 'Develop cutting-edge software and work on exciting projects.',
    contact: 'googlejobs',
    detailedDescription: 'Competitive pay and great benefits.\nContact: googlejobs@gmail.com',
    isExpanded: false,
  },
  {
    id: 5,
    postedBy: 'OpenAI',
    type: 'Internship',
    description: 'Work with top AI researchers and learn from the best.',
    contact: 'openai',
    detailedDescription: 'Summer internship for AI enthusiasts.\nContact: openai@gmail.com',
    isExpanded: false,
  },
  {
    id: 6,
    postedBy: 'Tesla',
    type: 'Student Worker',
    description: 'Help build the future of energy and transportation.',
    contact: 'tesla',
    detailedDescription: 'On-campus positions for students.\nContact: tesla@jobs.com',
    isExpanded: false,
  },
  // More opportunities...
];

const Opportunities = () => {
  const [opportunitiesData, setOpportunitiesData] = useState(opportunities);
  const [currentPage, setCurrentPage] = useState(1); // Track the current page
  const opportunitiesPerPage = 3; // Number of opportunities to display per page

  // Handle expanding and collapsing the opportunity description
  const toggleDescription = (id) => {
    setOpportunitiesData(opportunitiesData.map(opp =>
      opp.id === id ? { ...opp, isExpanded: !opp.isExpanded } : opp
    ));
  };

  // Get the opportunities for the current page
  const indexOfLastOpportunity = currentPage * opportunitiesPerPage;
  const indexOfFirstOpportunity = indexOfLastOpportunity - opportunitiesPerPage;
  const currentOpportunities = opportunitiesData.slice(indexOfFirstOpportunity, indexOfLastOpportunity);

  // Change the page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Calculate total pages
  const totalPages = Math.ceil(opportunitiesData.length / opportunitiesPerPage);

  return (
    <div className="container mt-5">
      <h1>Opportunities</h1>

      {/* Display each opportunity */}
      {currentOpportunities.map((opp) => (
        <div key={opp.id} className="opportunity-item mb-4 p-4 border rounded-lg shadow-md">
          <h3 className="text-xl font-bold">Posted by: {opp.postedBy}</h3>
          <p><strong>Type:</strong> {opp.type}</p>
          <p><strong>Description:</strong> {opp.description}</p>

          <button 
            className="btn btn-primary mt-2" 
            onClick={() => toggleDescription(opp.id)}
          >
            {opp.isExpanded ? 'Hide Details' : 'See Details'}
          </button>

          {opp.isExpanded && (
            <div className="mt-3">
              <h4 className="font-semibold">Opportunity</h4>
              <p>{opp.detailedDescription}</p>
            </div>
          )}

          {/* Message button */}
          <button className="btn btn-secondary mt-2">Message</button>
        </div>
      ))}

      {/* Pagination */}
      <div className="d-flex justify-content-center mt-4">
        <ul className="pagination">
          {/* Prev Button */}
          {currentPage > 1 && (
            <li className="page-item">
              <button className="page-link" onClick={() => paginate(currentPage - 1)}>
                Previous
              </button>
            </li>
          )}

          {/* Page Numbers */}
          {[...Array(totalPages).keys()].map((pageNumber) => (
            <li key={pageNumber + 1} className={`page-item ${currentPage === pageNumber + 1 ? 'active' : ''}`}>
              <button className="page-link" onClick={() => paginate(pageNumber + 1)}>
                {pageNumber + 1}
              </button>
            </li>
          ))}

          {/* Next Button */}
          {currentPage < totalPages && (
            <li className="page-item">
              <button className="page-link" onClick={() => paginate(currentPage + 1)}>
                Next
              </button>
            </li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default Opportunities;
