import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './Components/Home';
import User from './Components/User';
import Opportunities from './Components/Opportunities';
import Profile from './Components/Profile';
import AdminPanel from './Components/AdminPanel';
import LoginModal from './Components/LoginModal';
import SignupModal from './Components/SignupModal';
import UserProfile from './Components/UserProfile';
import AdminDropdown from "./Components/AdminDropdown";
import LoginStatusDropdown from "./Components/LoginStatusDropdown";
import './Styles/styles.css';

function App() {
  const [loginStatus, setLoginStatus] = useState('none'); 
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container">
            <Link className="navbar-brand" to="/">Webpage</Link>
            <button 
              className="navbar-toggler" 
              type="button" 
              data-bs-toggle="collapse" 
              data-bs-target="#navbarNav"
              aria-controls="navbarNav" 
              aria-expanded="false" 
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav me-auto">
                {/* Only show Users, Opportunities, Profile if logged in */}
                {(loginStatus !== 'none') && (
                  <>
                    <li className="nav-item">
                      <Link className="nav-link" to="/users">User</Link>
                    </li>
                    <li className="nav-item">
                      <Link className="nav-link" to="/opportunities">Opportunities</Link>
                    </li>
                    <li className="nav-item">
                      <Link className="nav-link" to="/profile">Profile</Link>
                    </li>
                  </>
                )}
                {/* Admin Dropdown Panel */}
                {loginStatus === 'admin' && <AdminDropdown />}
              </ul>

              {/* Right-Side Controls */}
              <div className="d-flex">
                {loginStatus === 'none' ? (
                  <>
                    <button className="btn btn-outline-primary me-2" onClick={() => setShowLogin(true)}>
                      Login
                    </button>
                    <button className="btn btn-outline-success" onClick={() => setShowSignup(true)}>
                      Sign Up
                    </button>
                  </>
                ) : (
                  <LoginStatusDropdown setLoginStatus={setLoginStatus} />
                )}
              </div>
            </div>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/users" element={<User />} />
          <Route path="/opportunities" element={<Opportunities />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/admin/approve-posted" element={<AdminPanel dataType="posted" />} />
          <Route path="/admin/approve-new-user" element={<AdminPanel dataType="newUser" />} />
          <Route path="/user/:userId" element={<UserProfile />} /> {/* Dynamic route for user profiles */}
        </Routes>

        {/* Toggle Login Status */}
        <div className="text-center mt-5">
          <p>Toggle login status:</p>
          <button className="btn btn-secondary me-2" onClick={() => setLoginStatus('none')}>
            Not Logged In
          </button>
          <button className="btn btn-secondary me-2" onClick={() => setLoginStatus('user')}>
            User Logged In
          </button>
          <button className="btn btn-secondary" onClick={() => setLoginStatus('admin')}>
            Admin Logged In
          </button>
        </div>

        {/* Modals */}
        <LoginModal show={showLogin} onClose={() => setShowLogin(false)} />
        <SignupModal show={showSignup} onClose={() => setShowSignup(false)} />
      </div>
    </Router>
  );
}

export default App;
