import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const UserProfile = () => {
  const { userId } = useParams(); // Get user ID from URL
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchUserProfile();
  }, [userId]); // Fetch user data when userId changes

  const fetchUserProfile = async () => {
    const token = localStorage.getItem('token');
    console.log("Fetching user with ID:", userId);
    try {
      // Fetch the specific user by their ID
      const response = await fetch(`http://138.197.99.80:2490/api/users/userId`, {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data); // Set the specific user's data in state
      } else {
        console.error('Failed to fetch user. Status:', response.status);
      }
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>; // Display loading while user data is being fetched
  }

  return (
    <div className="container mt-5">
      <h1>{user.first_name} {user.last_name}'s Profile</h1>
      <div className="card">
        <img
          src={user.profilePhoto || '/assets/user.jpg'} // Default image if no photo
          alt={user.first_name}
          className="card-img-top"
          style={{ height: '200px', objectFit: 'cover' }}
        />
        <div className="card-body">
          <h5 className="card-title">{user.first_name} {user.last_name}</h5>
          <p><strong>Major:</strong> {user.major}</p>
          <p><strong>School:</strong> {user.company}</p>
          <p><strong>Employment Status:</strong> {user.title}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>LinkedIn:</strong> {user.linkedin_link ? <a href={user.linkedin_link} target="_blank" rel="noopener noreferrer">Profile</a> : "No LinkedIn provided"}</p>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
